query getStreamMappingProperties {
  consume {
    id
    streamMappingProperties {
      displayName
      key
      type
      __typename
    }
    __typename
  }
}

mutation setupConnectorStream($streamId: ID!, $exportConfiguration: InputExportConfiguration) {
  consume {
    id
    setupConnectorStream(
      streamId: $streamId
      exportConfiguration: $exportConfiguration
    ) {
      id
      containerName
      mode
      exportOutgoingEdges
      exportIncomingEdges
      connector {
        id
        name
        accountDisplay
        connector {
          id
          name
          icon
          __typename
        }
        __typename
      }
      mappingConfiguration
      __typename
    }
    __typename
  }
}

mutation createToken($token: ApiTokenInput!) {
  administration {
    id
    createToken(token: $token) {
      name
      accessToken
      id
      __typename
    }
    __typename
  }
}

query getApiTokens {
  administration {
    id
    apiTokens {
      name
      accessToken
      id
      expiryDate
      expiresInDays
      __typename
    }
    __typename
  }
}

query entityTypeLayoutConfiguration($entityType: ID!) {
  management {
    id
    entityTypeConfiguration(entityType: $entityType) {
      ...EntityTypeConfiguration
      __typename
    }
    __typename
  }
}

fragment EntityTypeConfiguration on EntityTypeLayoutConfiguration {
  active
  displayName
  entityType
  icon
  id
  layoutConfiguration
  pageTemplateId
  path
  route
  template
  type
  __typename
}

query getRules(
	$searchName: String
	$isActive: Boolean
	$pageNumber: Int
	$sortBy: String
	$sortDirection: String
) {
	management {
		id
		rules(
			searchName: $searchName
			isActive: $isActive
			pageNumber: $pageNumber
			sortBy: $sortBy
			sortDirection: $sortDirection
		) {
			total
			data {
				id
				name
				order
				description
				isActive
				createdBy
				modifiedBy
				ownedBy
				createdAt
				modifiedAt
				author {
					id
					username
					__typename
				}
				__typename
			}
			__typename
		}
		__typename
	}
}

